//// Created by alice on 2023/10/20.//#include <printf.h>#include <string.h>#include "SqStack.h"/*中缀表达式：a*(b+c)-d/e后缀表达式：abc+*de/-前缀表达式：-*a+bc/de*/char* Algo_3_23_z(char* s);char* CharToStr_z(char c);char* StrToCat_z(char* a, char* b);int main(int argc, char* argv[]) {    char* s = "abc+*de/-"; //后缀表达式    printf("中缀表达式：%s\n", "a*(b+c)-d/e");    printf("后缀表达式："); //abc+*de/-    printf("%s\n", s);    printf("前缀表达式：");  // -*a+bc/de    printf("%s\n", Algo_3_23_z(s));//    char* s1 = "hello ";//    char* s2 = "world";//    char* s;////    s = StrToCat_z(s1, s2);//    printf("测试字符串拼接: \n%s", s);    return 0;}// 拼接字符串 a和b 返回拼接后的字符串char* StrToCat_z(char* a, char* b) {    char* s;    int i, j, alen, blen;    alen = (int) strlen(a);    blen = (int) strlen(b);    s = (char*) malloc((alen+blen+1) * sizeof (char));    j = 0;    for(i = 0; i < alen; i++) {        s[j++] = a[i];    }    for(i = 0; i < blen; i++) {        s[j++] = b[i];    }    s[j] = '\0';    return s;}/* * 将后缀表达式（逆波兰式）转换为前缀表达式（波兰式） * *【注】 * 表达式的变量为单字母，操作符只有'+'、'-'、'*'、'/'四则运算 */char* Algo_3_23_z(char* c) {    int i;    char* s, * e1, * e2;    SqStack S;    InitStack(&S);    for(i = 0; c[i] != '\0'; i++) {        s = CharToStr_z(c[i]);        //若c[i]是操作符(非操作数)，则栈中至少有两个元素        if(!isalpha(c[i])) {             if(Pop(&S, &e2) && Pop(&S, &e1)) {                 s = StrToCat_z(s, StrToCat_z(e1, e2));             } else {                 return NULL;             }        }        Push(&S, s);    }    Pop(&S, &s);    if(StackEmpty(S)) return s;    return NULL;}//将字符转换为字符串char* CharToStr_z(char c) {    char* s;    s = (char*) malloc(2 * sizeof(char));    s[0] = c;    s[1] = '\0';    return s;}